func (c *current) onField1(id, req, typ, name, def, annotations interface{}) (interface{}, error) {
	parsedID := -1
	if id != nil {
		parsedID = id.(int)
	}

	f := &Field{
		ID:          parsedID,
		Name:        string(name.(Identifier)),
		Type:        typ.(*Type),
		Annotations: toAnnotations(annotations),
	}
	if req != nil && !req.(bool) {
		f.Optional = true
	}
	if def != nil {
		f.Default = def.([]interface{})[2]
	}
	return f, nil
}

